#include "platform/voice.h"#import "VoiceOperation.h"#include <string>int Voice::StartRecord(const char* full_file_name,int secord){    NSString *strfullfilename = [NSString stringWithUTF8String:full_file_name];    int rev_val =  [VoiceOperation StartRecord:strfullfilename:secord];	return rev_val;}int Voice::StopRecord(){    int rev_val =  [VoiceOperation StopRecord];	return rev_val;}int Voice::StartPlay(const char* full_file_name,unsigned int voice_id, unsigned int lua_fun_id){    NSString *strfullfilename = [NSString stringWithUTF8String:full_file_name];    int rev_val =  [VoiceOperation StartPlay:strfullfilename:voice_id:lua_fun_id];	return rev_val;}int Voice::StopPlay(){    int rev_val =  [VoiceOperation StopPlay];	return rev_val;}int Voice::ReleaseVoice(){    int rev_val =  [VoiceOperation ReleaseVoice];	return rev_val;}